sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
if(inputs.req && inputs.req.state){
    if(inputs.req.state=="JAK"){
        outputs.jk="Y";
    }
    else{
        outputs.jk="N";
    }
}
else{
    outputs.jk="N";
}
})(inputs, outputs);

