sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
var req=inputs.req;
if(req.data)
req=req;
else
req=JSON.parse(req);
var path ="data.nomineeInfos."
try{
    if(req.data.nomineeInfos && req.data.nomineeInfos.length>0 ){
        var count=1;
        req.data.nomineeInfos.forEach(function(nominee){
            path=path+"["+count+"]."
            isFieldEmptyOrUndefined(nominee.frstNm,path+"frstNm");
            isFieldEmptyOrUndefined(nominee.lstNm,path+"lstNm");
            isFieldEmptyOrUndefined(nominee.dob,path+"dob");
            isFieldEmptyOrUndefined(nominee.relationship,path+"relationship");

//check if apointee is present

if(nominee.apnteDtls){
     isFieldEmptyOrUndefined(nominee.apnteDtls.frstNm,path+"apnteDtls.frstNm");
            isFieldEmptyOrUndefined(nominee.apnteDtls.lstNm,path+"apnteDtls.lstNm");
}
            count++;
        });
    }

}
catch(ex){
outputs.code=400;
outputs.msg=ex.message;

}



})(inputs, outputs);

function isFieldEmptyOrUndefined(field,path) {
    // Check if field is undefined, null, or an empty string
    if (field === undefined || field === null || field.trim() === '') {
         throw new Error("The field "+ path +" is mandatory");// Field is empty or undefined
    }
}

