sys_variable_value.value:
(function execute(inputs, outputs) {
// ... code ...
 
var jObj=inputs.request;

var jObj=inputs.request;
if(jObj.variantId=="VRN017"){
  if(!jObj.multiplier){
    var mul=10;
  }else{
    var mul=(jObj.multiplier);
  }

  var premium=parseInt(jObj.annualPremium);


    var sumAssured=inputs.sumAssured+"";
   // gs.info(sumAssured);
    sumAssured=sumAssured.toString().split(".")[0]+"";
  jObj.rider=[{
        "sumAssured":sumAssured,
        "name":"ADBY",
        "term":jObj.policyTerm+""
    },
    {
        "sumAssured":sumAssured,
        "name":"ACPD",
        "term":jObj.policyTerm+""
    }
    ]
}
 
//gs.info(jObj.product.coverDeatils.riderDetails.length)
if(!jObj.rider || !jObj.rider[0] || jObj.rider[0].name==""){
outputs.rider="";
return;
}
else{
  var builder = new sn_ih.XMLStreamingBuilder().build();
  builder.startElement("RiderDetails");
jObj.rider.forEach(function(element){
builder.startElement("Rider")
      builder.writeTextElement("Name",element.name)
      builder.writeTextElement("SA",element.sumAssured)
      builder.writeTextElement("Term", element.term)
    builder.endElement()    
     })
     builder.endElement();

var req= builder.getXMLString();
 var xmlDoc = new XMLDocument2();
   //  gs.info("0: " + req);
     xmlDoc.parseXML(req);
     var xmlString = req.toString();
     var xmlClean = global.JSUtil.unescapeText(xmlString);
    // gs.info("4: "+xmlClean)
     
     outputs.rider=xmlClean.toString().replace(/<\?xml.*\?>/g,'').replace(/<RiderDetails>|<\/RiderDetails>/g, '');
}
 
 
})(inputs, outputs);

