sys_variable_value.value:
(function execute(inputs, outputs) { //inputs clientRequest use parser in before step

//gs.info(JSON.parse(inputs.inputs))
if(typeof inputs.clientRequest === 'object'){
    var request = inputs.clientRequest.healthDetails;
      var   requestNri=inputs.clientRequest.nri;
}
else{
   var requestNri=JSON.parse(inputs.clientRequest).nri;
    var request = JSON.parse(inputs.clientRequest).healthDetails;
}


var checkForhealthdetails;
var checkForNri;

if(inputs.req.data){
checkForhealthdetails=inputs.req.data.healthDetails;
checkForNri=inputs.req.data.nri;
}
else{
    checkForhealthdetails=JSON.parse(inputs.req).data.healthDetails;
    heckForNri=JSON.parse(inputs.req).data.nri;
}

    
 if (!checkForhealthdetails || typeof checkForhealthdetails !== 'object') {
    return {
        code: 400,
        message: "Health questions missing"
    };
}
var nri=inputs.nri+"";
if(nri=="Y"){
    //gs.info("inside"+JSON.stringify(requestNri));
     if (!checkForNri || typeof checkForNri !== 'object') {
    return {
        code: 400,
        message: "Nri questions missing"
    };
}
}
////gs.info("1");
var nriMandatoryQuestions=[]
var nriParentArray=[];
var nriSubArray=[]
var mandatoryArray=[];
var parentQ=[];
var subQuestion=[];
var invaildQuestions=[];
var gr = new GlideRecord('x_rptp_smartinte_0_integration_question_configuration');
//var arr = [];
gr.addQuery('insurer', 'ad07033affc3c29019a8ffda7c4fd95e');
gr.query();
while (gr.next()) {
    if(gr.getValue('code')){
    var obj = {
         questionCategory: gr.getDisplayValue('question_category'),
         code: gr.getValue('code'),
         answer: gr.getValue('answer_number'),
         qes_id:gr.getValue('hdfc_id'),
        parent_q:gr.getDisplayValue("parent_question"),
        isMandatory:gr.getValue('mandatory'),
        answerCode:gr.getValue('answer_id') // code to send in answer for multiselect type answer
    };

    if(gr.getDisplayValue('question_category')=="NRI Questionnaire"){
        if(obj.parent_q ==""){
        nriParentArray.push(obj);
    }
    else{
        nriSubArray.push(obj);
        }
    }
    else if(obj.parent_q ==""){
        parentQ.push(obj);
    }
    else{
        subQuestion.push(obj);
        }
    }
    if (gr.getValue('mandatory')==1 || gr.getValue('mandatory_for')=="mandatory"){
        mandatoryArray.push(gr.getValue('code'));
    }
    
    if(gr.getValue('mandatory_for') !="none" || gr.getValue('mandatory_for')!=""){
        var gender=inputs.gender;
        var occ=inputs.occ;
        nri=inputs.nri;
        var flagForQuestion=true;
       //gs.addInfoMessage(gr.getValue('mandatory_for'));
        if(nri=="Y" && gr.getValue('mandatory_for')=="nri"){
           nriMandatoryQuestions.push(gr.getValue('code'));
        
        flagForQuestion=false;
        }
        if(gr.getValue('mandatory_for') =="female" && gender=="F"){
        mandatoryArray.push(gr.getValue('code'));
        flagForQuestion=false;
        }
         else if(gr.getValue('mandatory_for') =="occ" && (occ=="OCCT_SALR" || occ==" Self Employed" || occ=="OCCT_SEBS" || occ=="OCCT_AGCT"|| occ=="OCCT_RETD")){
        mandatoryArray.push(gr.getValue('code')); 
        flagForQuestion=false;
        }
         else if(gr.getValue('mandatory_for') =="occ" && occ=="OCCT_OTHR"){
        mandatoryArray.push(gr.getValue('code'));
        flagForQuestion=false;
        }
         else if(gr.getValue('mandatory_for') =="occ" && occ=="OCCT_STUD"){
        mandatoryArray.push(gr.getValue('code'));   
        flagForQuestion=false;
        }
        if (flagForQuestion){
            invaildQuestions.push(gr.getValue('code'));
        }
    }
  //  gs.addInfoMessage(JSON.stringify(obj))
 }
// gs.info("2"+ JSON.stringify(nriMandatoryQuestions));

//CHECK FOR MADNDATORY QUESTIONS 
const missingFields = mandatoryArray.filter(function(field) {
    return !request.find(function(item){
        return item.code === field
    })
   
});

if (missingFields.length > 0) {
    const fieldList = missingFields.join(', ');
    return {
        code: 400,
        message: `Mandatory health details are missing or empty: ${fieldList}`
    };
}

if(nri=="Y"){
