                        var fieldCount = j+2;
                        var fieldName = "answer"+fieldCount;

                        if(subInput == undefined) {
                        outputs.code = "400";
                        outputs.message = fieldName + " is mandatory for HDFQ13";  
                        return outputs;
                        }

                        if (validateAlphanumericLength(subInput, 0, 125)) {
                        outputs.code = "400";
                        outputs.message = "Answer must be between 0 and 125 for HDFQ13 for "+ fieldName;
                        return outputs;
                        }


                        if (validateTextLength(subInput, 0, 100)) {
                        outputs.code = "400";
                        outputs.message = "Answer must be between 0 and 100 characters for HDFQ13 for "+ fieldName;
                        return outputs;
                        }

                    }
                    
                } else {
                    ////gs.info("answer1 is No for HDFQ13, no subquestions to validate.");
                }
            } else {
                  outputs.code = "400";
        outputs.message = "answer1 should be either Yes or No for HDFQ13";
        return outputs;
            }
