 if (item.code == "HDFCQ59") {
            if (validateCheckbox(input)) {
                if (input === "Yes") {
                    var subQuestions = [
                        item.answer2,
                        item.answer3                    
                    ];

                    for (var j = 0; j < subQuestions.length; j++) {
                        var subInput = subQuestions[j];

                        var fieldCount = j+2;
                        var fieldName = "answer"+fieldCount;

                        if(subInput == undefined) {
                        outputs.code = "400";
                        outputs.message = fieldName + " is mandatory for HDFCQ59";  
                        return outputs;
                        }

                        if (!validateCheckbox(subInput)) { 
                        outputs.code = "400";
                        outputs.message = "Answer must be either 'Yes' or 'No' for HDFCQ59 for "+ fieldName;
                        return outputs;
                    }

                    }
                    
                } else {
                    ////gs.info("answer1 is No for HDFCQ59, no subquestions to validate.");
                }
                
            } else {
                  outputs.code = "400";
        outputs.message = "answer1 should be either Yes or No for HDFCQ59";
        return outputs;
            }
        }

    }
 

