var gr = new GlideRecord('x_rptp_smartinte_0_integration_option_mapper');
    gr.addQuery("insurer","ad07033affc3c29019a8ffda7c4fd95e");
    gr.addQuery("sourcefieldmap","73a1981f470b0a50e9ddbf66706d43cf").addOrCondition("sourcefieldmap","ca7eeabcff5c129019a8ffda7c4fd983").addOrCondition("sourcefieldmap","20f2727847901a50e9ddbf66706d4354").addOrCondition("sourcefieldmap","6104fd7747f78250e9ddbf66706d43bd").addOrCondition("sourcefieldmap","6ad4da34ff98129019a8ffda7c4fd94e").addOrCondition("sourcefieldmap","57f4b2bc47901a50e9ddbf66706d439a");
    gr.query();
var insurer="HDFC";
var opArr = [];  
    while (gr.next()) {
        var obj = {
            insurer:gr.getDisplayValue('insurer'),
            source: gr.getDisplayValue("sourcefieldmap"),
            option: gr.getDisplayValue("option"),
            value: gr.getDisplayValue('value')
        }
        if(gr.getDisplayValue('insurer')==insurer)
        opArr.push(obj);
    }
function mapper(insurer,option,fieldName){   //insurer=name of insurer ,, option==value from the payload   ,  fieldName==name in sourceField mapper
    for (var i = 0; i < opArr.length; i++) {
            if (opArr[i].insurer==insurer && opArr[i].source == fieldName && opArr[i].option == option) {
               // ////gs.info("123")
                var name= opArr[i].value;
                return name;
            }    
        }
        }

var questionArray=[]; // final questionaire after transformation 

var impairmentParentArray=[];
var impairmentSubQuestionArray=[];
//var checkForImpairment=[];  //to collect all the impairment deatails
var checkForImpairmentIspresent=false
healthDetails.forEach(function(question){
    //var id;
    var fetchQuestionParentArray=filterByCode(parentQ,question.code);
   // gs.addInfoMessage(JSON.stringify(fetchQuestionParentArray));
        fetchQuestionParentArray.forEach(function(element  /* traverse each parent question */){
            var value=question[element.answer];
            if(value=="true" || value=="Yes"){
                value="Y"
            }
            else if(value=="false" ||value=="No"){
                value="N"
            }
            if(question.code=="HDFQ6"){   //industry type
                  var indusType=value;
                  value=mapper(insurer,indusType,"Industry");
                }
                else if (question.code=="HDFQ1_1"){  //occ type
                  var occ=value;
                  value=mapper(insurer,occ,"Occupation");
                }
                else if (question.code=="HDFQ36"){ //height
                  var height=value;
                  value=mapper(insurer,height,"Height");
                }
                else if (question.code=="HDFQ35"){ //Medicalexamlocation
                  var height=value;
                  value=mapper(insurer,height,"Medicalexamlocation");
                }
                else if (question.code=="HDFQ37"){ //weight
                  var weight=value;
                  value=mapper(insurer,weight,"Weight");
                }
                else if (question.code=="HDFQ1_3"){ //designation
                  var occSub=value;
                  value=mapper(insurer,occSub,"Designation");
                }

           var parentQuestionObj={
            "questionans": value,
            "questionid":element.qes_id+""
           }
            if(element.code=="HDFCQ55"){
                //var questionAns=[];
                checkForImpairment=filterSubQuestion(subQuestion,question.code);
                if(question.answer1=="true"|| question.answer1=="Yes")
                checkForImpairmentIspresent=true;
            }
            questionArray.push(parentQuestionObj);
            if((question.answer1=="true"||question.answer1=="Yes") && question.code!="HDFCQ55"){     //only if parent question is true check for its child questions
                var relatedSubQuestion=filterSubQuestion(subQuestion,question.code);
               //  gs.addInfoMessage(JSON.stringify(relatedSubQuestion));
                relatedSubQuestion.forEach(function(subQuest){
                        var subQuestionObj={   
                        "questionans": question[subQuest.answer] +"",
                        "questionid":subQuest.qes_id+""
                        };
                        questionArray.push(subQuestionObj);
                    });
            }   
        });  
        if(question.code=="HDFCQ56" || question.code=="HDFCQ57" || question.code=="HDFCQ58" || question.code=="HDFCQ59"||question.code=="HDFCQ55"){
            relatedSubQuestion=filterSubQuestion(subQuestion,"HDFCQ55");
            relatedSubQuestion=filterByCode(relatedSubQuestion,question.code)
                var questionId="";
                var questionAns=[];
                relatedSubQuestion.forEach(function(impairmentSubQues){
                    if(question[impairmentSubQues.answer]=="true" || question[impairmentSubQues.answer]=="Yes"){
                        questionAns.push(impairmentSubQues.answerCode);
                    }
                });

                questionAns=questionAns.join(",");
              
                questionId=relatedSubQuestion[0].qes_id.toString();
                
                var impairmentParentObj={
                    "questionans": "N",
                    "questionid":questionId.replace("Details","")
                }
                if(question.code!="HDFCQ55")
                impairmentParentArray.push(impairmentParentObj)
                 var impairmentsubObj={
                    "questionans": questionAns||"N",
                    "qtype": "multiselect",
                    "questionid":questionId
                }
                impairmentSubQuestionArray.push(impairmentsubObj);
                //questionArray.push(impairmentObj);
        }
    });

    if(checkForImpairmentIspresent){                //checcking for impairment deatails is true or false to add in the array
    impairmentParentArray.forEach(function(parentObj){
        parentObj.questionans="Y";
    });
    questionArray=questionArray.concat(impairmentSubQuestionArray);
    }
    else{
        impairmentParentArray.forEach(function(parentObj){
        parentObj.questionans="N";
    });
    }
   questionArray= questionArray.concat(impairmentParentArray);
//gs.addInfoMessage(JSON.stringify(questionArray))


    if(inputs.nri =="Y"){
        var residenseQuestion={
            "questionans": inputs.commCountry+"",
            "questionid":"HDFCPart2Section.NRICountryOfResidence"
        } 
       questionArray.push(residenseQuestion); 
    }
    builderObj.uwdetails=questionArray;
    outputs.request=builderObj;




   // ////gs.info("beforeetest"+JSON.stringify(builderObj))


//for nri question 
// if(inputs.clientRequest.suitabilityMatrix.nationalty=="Others"){
if((! inputs.clientRequest.nri ||  inputs.clientRequest.nri.length== 0) && (inputs.nri=="false" || inputs.nri=="N")){
   // //gs.info("test"+JSON.stringify(builderObj))
   outputs.request=builderObj;
   return ;
 }

var nriArray=inputs.clientRequest.nri;
var nriQuestionArray=[];
nriArray.forEach(function(nriQuestion){
        var fetchQuestionParentArray=filterByCode(nriParentArray,nriQuestion.code);
        fetchQuestionParentArray.forEach(function(element  /* traverse each parent question */){
            var value=nriQuestion[element.answer];
