           if((nriQuestion.answer1=="true"||nriQuestion.answer1=="Yes")){     //only if parent question is true check for its child questions
                var relatedSubQuestion=filterSubQuestion(nriSubArray,nriQuestion.code);
               //  gs.addInfoMessage(JSON.stringify(relatedSubQuestion));
                relatedSubQuestion.forEach(function(subQuest){
                        var subQuestionObj={   
                        "questionans": nriQuestion[subQuest.answer] +"",
                        "questionid":subQuest.qes_id+""
                        };
                        nriQuestionArray.push(subQuestionObj);
                    });
            } 
});
});

nriQuestionArray.push(
    {
