function validateRadioButton(answer) {
        return answer === 'Yes' || answer === 'No';
    }

    function validateCheckbox(answer) {
    return answer === 'Yes' || answer === 'No';
}


    function validateTextLength(text, minLength, maxLength) {
        if (text)
        return text.length < minLength || text.length > maxLength;
        else true
    }
 
       function validateNumericLength(number, minLength, maxLength) {
    var numberStr = String(number);
    if (numberStr === "undefined" || numberStr.length === 0) {
        return true; 
    }
    return numberStr.length < minLength || numberStr.length > maxLength;
}
 
    function validateDropdown(selection, allowedValues) {
        return !selection || allowedValues.includes(selection);
    }

    function validateAlphanumericLength(text, minLength, maxLength) {
    if (typeof text !== 'string' || !text) {
        return true; 
    }

    if (text.length < minLength || text.length > maxLength) {
        return true; 
    }
    var isAlphanumeric = /^[a-zA-Z0-9]*$/.test(text);
    return !isAlphanumeric; 
}



function filterByCode(arr, code) {
return arr.filter(function(ques) {
  return ques.code === code;
});
}


function filterSubQuestion(subQuestionArray, code) {
    return subQuestionArray.filter(function(ques) {
        return ques.parent_q === code;
});
}

