sys_script_include.script:
var ICICI_IPRU_Rider_Validation = Class.create();
ICICI_IPRU_Rider_Validation.prototype = {
    initialize: function() {},

    type: 'ICICI_IPRU_Rider_Validation',
    validate: function(riderObj, variantSysId,req) {
        //var variantId = "VRN017";
        var gr = new GlideRecord("x_rptp_smartinte_0_integration_rider_per_variant");
        gr.addQuery("sys_id", variantSysId+"");
		gs.info("sysidsent "+variantSysId);
        gr.query();
        if (gr.next()) {

			gs.info("ridertest"+JSON.stringify(riderObj));
			gs.info("reqbody "+req.age);

            if (parseInt(riderObj.term) > parseInt(gr.u_max_pt)) {
                riderObj.term = gr.u_max_pt+"";
            }
			if(parseInt(riderObj.ppt)>parseInt(riderObj.term))
			{
				gs.info("rider term: "+riderObj.term);
				riderObj.ppt = riderObj.term;
			}
			gs.info("rider new name: "+riderObj.name);
			if(riderObj.name == "RD022")
			{ 
				gs.info("rider sumAssured: "+riderObj.sumAssured+"");
            if (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured)) {
                riderObj.sumAssured = gr.u_max_sumassured+"";
            }

			}
			if(riderObj.name == "RD021")
			{ 
            if (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured)) {
                riderObj.sumAssured = gr.u_max_sumassured+"";
            }

			}

        }
		gs.info("sinchana");
        return riderObj;
    }





};
