sys_script_include.script:
var hdfcRiderValidation = Class.create();
hdfcRiderValidation.prototype = {
    initialize: function() {},


    validate: function(riderObj, sys_id, req,riderName) {

		try{
			var varientId = req.variantId;
        var sumAssurd = (parseInt(req.sumAssured) / 2);
// gs.info("riderobj" + JSON.stringify(riderObj));
// gs.info("sysiddddddddd  " + sys_id);
// gs.info("reqqqqqqqqqqq" + JSON.stringify(req));


        var gr = new GlideRecord('x_rptp_smartinte_0_integration_rider_per_variant');
        gr.addQuery("sys_id", sys_id);
        gr.query();

        if (gr.next()) {
			//gs.info("hii");
			if(gr.u_max_pt)
            riderObj.term = parseInt(riderObj.term) > parseInt(gr.u_max_pt) ? gr.u_max_pt+"" : riderObj.term;
			var maxPt=(75 - parseInt(req.age))*riderObj.mul ;
			maxPt=maxPt.toString().split(".")[0];
			riderObj.term=calculateMin(riderObj.term,maxPt);


			if(gr.max_ppt)
            riderObj.ppt = parseInt(riderObj.ppt) > parseInt(gr.max_ppt) ? gr.max_ppt+"" : riderObj.ppt;
			var maxPPt=(75 - parseInt(req.age)) * riderObj.mul;
			maxPPt=maxPPt.toString().split(".")[0];
			riderObj.ppt=calculateMin(riderObj.ppt,maxPPt);


            if (gr.u_max_sumassured && (varientId == "VRN013" || varientId == "VRN015" || varientId == "VRN016")){
				if(parseInt(riderObj.sumAssured) < parseInt(sumAssurd)){
						throw new Error("Min Rider SumAssured for rider("+riderName+ ") is "+ sumAssurd);
				}
                riderObj.sumAssured = (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured+"")) ? (gr.u_max_sumassured+"") : riderObj.sumAssured ;
			riderObj.sumAssured= calculateMin(riderObj.sumAssured,req.sumAssured);

            } else if (gr.u_max_sumassured && varientId == "VRN014") {
				if(parseInt(riderObj.sumAssured) < parseInt(sumAssurd)){
						throw new Error("Min Rider SumAssured for rider("+riderName+ ") is "+ sumAssurd);
				}
                riderObj.sumAssured = (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured+"")) ? (gr.u_max_sumassured+"") : riderObj.sumAssured ;
			riderObj.sumAssured= calculateMin(riderObj.sumAssured,req.sumAssured);
            }
        }
		//gs.info(riderObj);
        return riderObj;
		}
		catch(ex){
			return JSON.stringify({code:400,
			message:ex.message});
		}

    },
    type: 'hdfcRiderValidation',
};

function calculateMin(value1,value2){
	//gs.info("value1"+value1);
	//gs.info("value2"+value2);

	if(parseInt(value1) < parseInt(value2))
	return value1;
	else
	return value2;
}
