sys_script_include.script:
var Balic_IPG_Rider_Validation = Class.create();
Balic_IPG_Rider_Validation.prototype = {
    initialize: function() {},

    type: 'Balic_IPG_Rider_Validation',
    validate: function(riderObj, variantSysId,req) {
        //var variantId = "VRN029";
		// try{ 
        var gr = new GlideRecord("x_rptp_smartinte_0_integration_rider_per_variant");
        gr.addQuery("sys_id", variantSysId+"");
		gs.info("sysidsent "+variantSysId);
        gr.query();
        if (gr.next()) {

			// gs.info("ridertest"+JSON.stringify(riderObj));
			// gs.info("reqbody "+JSON.stringify(req));
			// if(parseInt(riderObj.sumAssured) < parseInt(req.sumAssured))
			// {
			// 	throw new Error("Min Rider SumAssured for rider("+rider.name+ ") is "+ req.sumAssured);
			// }
			if (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured)) {
                riderObj.sumAssured = gr.u_max_sumassured+"";
            }

			if(parseInt(riderObj.term) > parseInt(req.policyTerm))
			{
				riderObj.term = req.policyTerm+"";
			}
			if(parseInt(riderObj.ppt) > parseInt(req.paymentTerm))
			{
				riderObj.term = req.paymentTerm+"";
			}
            if (parseInt(riderObj.term) > parseInt(gr.u_max_pt)) {
                riderObj.term = gr.u_max_pt+"";
            }
			if(parseInt(riderObj.ppt)>parseInt(riderObj.term))
			{
				riderObj.ppt = riderObj.term;
			}
			gs.info("rider new name: "+riderObj.name);
			if(riderObj.name == "R080A01")
			{ 

			if(parseInt(riderObj.sumAssured) != parseInt(req.sumAssured *3) && parseInt(riderObj.sumAssured) < parseInt(gr.u_max_sumassured))
			{
				riderObj.sumAssured = (req.sumAssured *3)+"";
			}
            // if (parseInt(riderObj.sumAssured) > parseInt(gr.u_max_sumassured)) {
            //     riderObj.sumAssured = gr.u_max_sumassured+"";
            // }

			}
			if(riderObj.name == "R081A01")
			{ 
            if (parseInt(riderObj.sumAssured) != parseInt(req.sumAssured) && parseInt(riderObj.sumAssured) < parseInt(gr.u_max_sumassured)) {
                riderObj.sumAssured = req.sumAssured+"";
            }
			}

        }
		gs.info("rider body ready for return "+JSON.stringify(riderObj));
        return riderObj;
    // }
	// catch(ex)
	// {
	// 	return JSON.stringify({code:400,
	// 		message:ex.message});
	// }
	}





};
