sys_script_include.script:
var ICICI_IPRU_Rider_Validation = Class.create();
ICICI_IPRU_Rider_Validation.prototype = {
    initialize: function() {},

    type: 'ICICI_IPRU_Rider_Validation',
    validate: function(riderObj, variantSysId, req) {

        var gr = new GlideRecord("x_rptp_smartinte_0_integration_rider_per_variant");
        gr.addQuery("sys_id", variantSysId+"");
        gs.info("sysidsent " + variantSysId);
        gr.query();

        if (gr.next()) {
            gs.info("ridertest " + JSON.stringify(riderObj));
            gs.info("reqbody " + req.age);


            if (riderObj.name == "RD022" || riderObj.name == "RD021") {
                gs.info("rider sumAssured: " + riderObj.sumAssured + "");
                var maxSumAssured = parseInt(gr.u_max_sumassured);
                var riderSumAssured = parseInt(riderObj.sumAssured);


                if (riderSumAssured > maxSumAssured) {
                    var maxSumAssuredFormatted = maxSumAssured.toLocaleString(); // Format max sum assured with 
                    gs.error("Error: Max sum assured is " + maxSumAssuredFormatted + ". It should not be more than that.");
                    throw new Error("Max sum assured is " + maxSumAssuredFormatted + ". It should not be more than that.");
                }
            }
        }
        gs.info("sinchana");
        return riderObj;
    }
};
